        }
      ]
    },
    "streamSettings": {
      "network": "tcp"  
    },
    "tag": "DOUS",
    "proxySettings": {
        "tag": "DOSG"  
      }
  },
  "outboundDetour": [
    {
      "protocol": "shadowsocks",
      "settings": {
        "servers": [
          {
